<template>
    <div id="jftime">
        <div class="container-full">
            <el-container>
                <div class="col-xs-6 col-md-8" id="audio">
                    <div class="audio_menu">
                        <div class="audio_img">
                            <div id="player" class="aplayer"></div>
                        </div>
                    </div>
                </div>
                <div class="cons">
                   <el-main>
                        <div id="header-son" class="row">
                            <div class="col-xs-12 col-sm-6 col-md-12" id="jieshao">

                            </div>
                        </div>

                        <ul id="audio_list">
                            <el-divider content-position="left" class="divider col-md-12 col-xs-8">
                                江峰漫谈<i class="el-icon-microphone"></i>
                            </el-divider>
                            <li class="item">
                                <div class="jiemu row">
                                    <div class="col-xs-6 col-md-2" v-for="todo in jfmt" :key="todo.id">
                                        <el-image :src="todo.cover_src">
                                          <div slot="placeholder" class="image-slot">
                                            加载中<span class="dot">...</span>
                                          </div>
                                        </el-image>
                                        <i :class="index===todo.id ? 'el-icon-video-pause':btnStyle" @click="playAudio(todo.audio_src,todo.audio_name,todo.creator,todo.id)"></i>
                                        <h4 class="title">
                                            {{todo.title}}
                                        </h4>
                                        <h6 class="date">{{todo.upload_date}}</h6>
                                    </div>
                                </div>
                                <!--分页-->
                                <div class="block">
                                  <el-pagination
                                    layout="prev, pager, next"
                                    :page-size="5"
                                    :total="data1_len"
                                    @current-change="handleCurrentChange1"
                                  >
                                  </el-pagination>
                                </div>
                            </li>
                            <el-divider content-position="left">
                                历史上的今天<i class="el-icon-ship"></i>
                            </el-divider>
                            <li class="item">
                                <div class="jiemu row">
                                    <div class="col-xs-6 col-md-2" v-for="todo in today_history" :key="todo.id">
                                        <el-image :src="todo.cover_src">
                                          <div slot="placeholder" class="image-slot">
                                            加载中<span class="dot">...</span>
                                          </div>
                                        </el-image>
                                        <i :class="index===todo.id ? 'el-icon-video-pause':btnStyle" @click="playAudio(todo.audio_src,todo.audio_name,todo.creator,todo.id)"></i>
                                        <h4 class="title">
                                            {{todo.title}}
                                        </h4>
                                        <h6 class="date">{{todo.upload_date}}</h6>
                                    </div>
                                </div>
                                <!--分页-->
                                <div class="block">
                                  <el-pagination
                                    layout="prev, pager, next"
                                    :page-size="5"
                                    @current-change="handleCurrentChange2"
                                    :total="data2_len">
                                  </el-pagination>
                                </div>
                            </li>
                            <el-divider content-position="left">
                                川普推推推<i class="el-icon-chat-dot-square"></i>
                            </el-divider>
                            <li class="item">
                                <div class="jiemu row">
                                    <div class="col-xs-6 col-md-2" v-for="todo in trump_twitter" :key="todo.id">
                                        <el-image :src="todo.cover_src" :fit="fits">
                                          <div slot="placeholder" class="image-slot">
                                            加载中<span class="dot">...</span>
                                          </div>
                                        </el-image>
                                        <i :class="index===todo.id ? 'el-icon-video-pause':btnStyle" @click="playAudio(todo.audio_src,todo.audio_name,todo.creator,todo.id)"></i>
                                        <h4 class="title">
                                            {{todo.title}}
                                        </h4>
                                        <h6 class="date">{{todo.upload_date}}</h6>
                                    </div>
                                </div>
                                <!--分页-->
                                <div class="block">
                                  <el-pagination
                                    layout="prev, pager, next"
                                    :page-size="5"
                                    @current-change="handleCurrentChange3"
                                    :total="data3_len">
                                  </el-pagination>
                                </div>
                            </li>
                            <el-divider></el-divider>
                        </ul>
                    </el-main>
                </div>
                <el-footer></el-footer>
            </el-container>
        </div>
    <router-view/>
    </div>
</template>
<style scoped>

    * {
        padding: 0;
        margin: 0;
        font-family: '苹方';
    }
    #jftime {
        font-family: 'Avenir', Helvetica, Arial, sans-serif;
        -webkit-font-smoothing: antialiased;
        -moz-osx-font-smoothing: grayscale;
        text-align: center;
        padding: 0;
        margin: 0;
        background: #f7f7f7;
    }
    /*中部内容区*/
    .cons{
        width: 100%;
        height: auto;
        justify-content: center;
        align-items: center;
        margin: 3rem auto;
    }
    .el-main{
        width: 100%!important;
        padding: 0!important;
        max-width: 1260px!important;
        margin: 0 auto;
        background: #f5f5f5!important;
    }
    .col-xs-12{
        width: 89%!important;
        margin: 0 auto!important;
    }
    #header-son{
        display: flex;
        justify-content: space-between;
        align-items: center;
    }
    #jieshao{
        display: flex;
        justify-content: flex-start;
        align-items: center;
    }
    #jieshao img{
        width: 130px;
        height: 40px;
    }
    .audio_menu{
        position: fixed;
        right: 0;
        top: 6rem;
        width: auto;
        border: 0!important;
        z-index: 200;
    }
    /*节目卡片的样式*/
    #audio_list{
        width: 100%;
        height: auto;
        list-style: none;
    }
    #audio_list .el-divider i{
        margin-left: .5rem;
    }
    #audio_list .item{
        height: auto;
    }
    #audio_list .item h3{
        height: 3rem;
        margin: 0;
        text-align: left;
        line-height: 3rem;
    }
    #audio_list .item .jiemu{
        width: 100%;
        height: auto;
        margin: 0;
        display: flex;
        justify-content: center;
        align-items: center;
    }
    #audio_list .item .jiemu .col-xs-6{
        width: 40%!important;
        padding: .5rem!important;
        margin: 0 .5rem .5rem .5rem!important;
        display: flex;
        justify-content: flex-start;
        align-items: center;
    }
    .col-xs-6 .el-image{
        width: 100%!important;
        height: 100%;
    }
    .col-xs-6 .title{
        height: 3.8rem;
        display: -webkit-box;
        -webkit-box-orient: vertical;
        -webkit-line-clamp: 3;
        line-height: 1.3rem;
        overflow: hidden;
        margin-bottom: 1rem;
    }
    .col-md-2{
        width: 100%!important;
        padding: .5rem!important;
        margin: 0 .5rem .5rem .5rem!important;
    }
    #audio_list .item .jiemu div{
        display: flex;
        flex-direction: column;
        justify-content: space-between;
        align-items: center;
        position: relative;
        transition: all .5s;
        background: #fff;
    }
    #audio_list .item .jiemu div .el-image{
        width: 10rem;
        height: 6rem;
        margin-bottom: .5rem;
    }
    .title{
        width: 100%;
        height: 3rem;
        text-align: justify;
        font-size: .64rem;
        /*word-break: break-all;*/
        font-family: "Helvetica Neue", Helvetica, "PingFang SC", "Hiragino Sans GB", "Microsoft YaHei", "微软雅黑", Arial, sans-serif;
        display: -webkit-box;
        -webkit-box-orient: vertical;
        -webkit-line-clamp: 3;
        line-height: 1rem;
        overflow: hidden;
        margin-bottom: 1rem;
    }
    .date{
        width: 100%;
        text-align: left;
        font-size: .64rem;
        margin: 0;
        font-family: '楷体';
    }
    /*底部样式*/
    .el-footer {
        height: 3rem !important;
        color: #fff!important;
        z-index: 100;
        display: flex;
        flex-direction: column;
        justify-content: space-around;
    }

    #nav ul{
        padding: 0;
        margin: 0;
        list-style: none;
    }
    #nav a{
        text-decoration: none;
        font-weight: bold;
        color: #2c3e50;
    }
    .items{
        padding: .5rem 1rem;
    }
    .nav-item{
        display: flex;
        justify-content: center;
        align-items: center;
    }

    .navbar-collapse{
        flex-grow: 0 !important;
    }

    .navbar{
        display: flex !important;
        justify-content: space-between !important;
        padding: .5rem 0 !important;
    }
    .el-carousel__item h3 {
        color: #475669;
        font-size: 18px;
        opacity: 0.75;
        line-height: 300px;
        margin: 0;
      }
    .el-carousel__item:nth-child(2n) {
        background-color: #99a9bf;
    }

    .el-carousel__item:nth-child(2n+1) {
        background-color: #d3dce6;
    }
    .el-pagination{
        width: 100%;
        height: 50px;
        display: flex;
        align-items: center;
        justify-content: center;
    }
    .el-pager{
        background: #fff;
    }
    .el-pagination li{
        margin: 0!important;
    }
    /*节目卡片浮起特效*/
    .active{
        box-shadow: 0 6px 12px 0 rgba(0, 0, 0, 0.1)!important
    }
    .el-image{
        padding: 0!important;
        box-shadow: none!important;
    }
    .el-icon-video-play,.el-icon-video-pause{
        position: absolute;
        bottom: .3rem;
        right: .3rem;
        font-size: 1.6em;
        color: #44bfa3;
        opacity: .6;
    }
</style>
<script>
    import APlayer from 'aplayer'
    export default {
      name: "JFTime",
      data() {
          return {
            btnStyle:'el-icon-video-play',
            visible: false,
            active: '',
            id:'',
            logo_src:'http://120.77.223.229:100/JfTime/net_img/JF_logo.png',
              jfTime:'http://120.77.223.229:100/JfTime/net_img/jfTime.png',
            index:0,
            play:0,
            audio_menu:'',
            flag: false,
            demo: '',
            // 节目总数
            data1_len: 0,
            data2_len: 0,
            data3_len: 0,
            jfmt:"",
            trump_twitter:"",
            today_history:"",
              fits:'fill'
        };
      },
      methods:{
          //  上下翻页
          handleCurrentChange1(val){
              var offset = (val-1)*5
            fetch("/ajax/jftime/handleCurrentChange1?offset="+offset).then(function (e) {
                return e.json()
            }).then((e)=>{
                this.jfmt = e
            })
        },
          handleCurrentChange2(val){
            var offset = (val-1)*5
            fetch("/ajax/jftime/handleCurrentChange2?offset="+offset).then(function (e) {
                return e.json()
            }).then((e)=>{
                this.today_history = e
            })
        },
          handleCurrentChange3(val){
            var offset = (val-1)*5
              var t_name = 'trump_twitter'
            fetch("/ajax/jftime/handleCurrentChange3?offset="+offset).then(function (e) {
                return e.json()
            }).then((e)=>{
                this.trump_twitter = e
            })
        },
          // 音频控件
          playAudio(audio_src,audio_name,creator,index){
            this.index = index
            this.demo.destroy()
            const ap = new APlayer({
                container: document.getElementById('player'),
                fixed: false,
                    autoplay: true,
                    listFolded: true,
                audio: [
                    {name: audio_name,
                    artist: creator,
                    url: audio_src,
                    cover: '',
                    }
                ]
                })
            ap.on('pause', ()=>{
                this.index = ''

            });
            ap.on('play', ()=>{
                this.index = index
            });
            console.log(this.index)
            this.demo = ap
        }
      },
      async mounted() {
        const ap = new APlayer({
            container: document.getElementById('player'),
            fixed: false,
            audio: [{
            name: '曲名',
            artist: '创作者',
            url: '',
            cover: 'https://cn-south-17-aplayer-46154810.oss.dogecdn.com/yourname.jpg',
            }]
        })
        this.demo = ap
              // 获取音频
          fetch("/ajax/jftime/dataAudio").then(function (e) {
                return e.json()
          }).then((e)=>{
              this.jfmt = e[0]
              this.today_history = e[1]
              this.trump_twitter = e[2]
          }),
              //  获取节目数
          fetch("/ajax/jftime/dataLength").then(function (e) {
              return e.json()
          }).then((e)=>{
              this.data1_len = e['data1_len']
              this.data2_len = e['data2_len']
              this.data3_len = e['data3_len']
          })
        }
  }
</script>
