/* eslint-disable*/
<template>
    <div id="HotAudio">
        <div class="container-full">
            <el-container>
                <div id="center">
                    <el-main>
                        <ul class="jf_audio">
                            <div class="divider col-md-12 col-xs-8">
                                <h4>
                                            <span>近期热门</span>
                                            <i class="iconfont icon-hot_icon"></i>
                                        </h4>
                                <h5>YouTube上面质量最高的时政评论节目之一。</h5>
                            </div>
                            <li class="item">
                                        <div class="jiemu row">
                                            <div style="width: 300px" class="col-xs-6 col-sm-2" v-for="todo in first_page_hotAudios" :key="todo.title">
                                                <el-image :src="todo.cover_src">
                                                  <div slot="placeholder" class="image-slot">
                                                    加载中<span class="dot">...</span>
                                                  </div>
                                                </el-image>
                                                <i :class="index===todo.id ? 'el-icon-video-pause':btnStyle" @click="playAudio(todo.audio_src,todo.audio_name,todo.creator,todo.id)"></i>
                                                <h4 class="title">
                                                    {{todo.title}}
                                                </h4>
                                                <h6 class="date">{{todo.upload_date}}</h6>
                                            </div>
                                        </div>
                                        <!--分页-->
                                        <div class="block">
                                          <el-pagination
                                            layout="prev, pager, next"
                                            :page-size="5"
                                            :total="dataLength"
                                            @current-change="handleCurrentChange1"
                                          >
                                          </el-pagination>
                                        </div>
                                    </li>
                        </ul>
                    </el-main>
                </div>
            </el-container>
        </div>
    </div>
</template>
<style scoped>
    * {
        padding: 0;
        margin: 0;
        font-family: Helvetica,"PingFang SC","Hiragino Sans GB","Microsoft YaHei","微软雅黑",Arial,sans-serif;
    }
    #HotAudio {
        width: 100%;
        font-family: 'Avenir', Helvetica, Arial, sans-serif;
        -webkit-font-smoothing: antialiased;
        -moz-osx-font-smoothing: grayscale;
        text-align: center;
        padding: 0;
        margin: 0;
        background: #f7f7f7;
    }
    .el-container{
        display: flex;
        flex-direction: column;
        justify-content: flex-start;
        align-items: center;
    }
    /*音频卡片样式*/
    #center{
        width: 100%;
        background: #f5f5f5!important;
        display: flex;
        justify-content: center;
        align-items: center;
        height: auto;
    }
    .el-main{
        width: 100%!important;
        max-width: 1260px!important;
        margin: 0 auto;
        padding: 0 1.5rem;
    }
    /*播放按钮*/
    .audio_menu{
        position: fixed;
        height: 3.5rem;
        left: 0;
        right: 0;
        top: 0;
        margin: 0 auto;
        width: 15rem;
        border: 0!important;
        z-index: 1200;
    }
    .jf_audio{
        width: 100%;
        height: auto;
        list-style: none;
        margin: 1rem 0;
    }
    .jf_audio .el-divider i{
        margin-left: .5rem;
    }
    .jf_audio .item{
        width: 100%;
        height: auto;
        position: relative;
    }
    .jf_audio .item h3{
        height: 3rem;
        margin: 0;
        text-align: left;
        line-height: 3rem;
    }
    .jf_audio .item .jiemu{
        height: auto;
        margin: 0;
        display: flex;
        justify-content: space-between;
        align-items: center;
    }
    .jf_audio .item .jiemu .col-sm-2{
        margin-bottom: 1rem;
        margin-right: 2.5rem;
        display: flex;
        justify-content: space-between;
        align-items: center;
        padding: 0!important;
    }
    .jf_audio .item .jiemu .col-sm-2:last-child{
        margin-right: 0;
    }
    .jf_audio .item .jiemu div{
        display: flex;
        flex-direction: column;
        justify-content: space-between;
        align-items: center;
        position: relative;
        transition: all .5s;
        background: #fff;
    }
    .jf_audio  .item .jiemu div .el-image{
        width: 10rem;
        height: 6rem;
        padding: .5rem!important;
    }
    .title{
        width: 100%;
        height: 3rem;
        text-align: justify;
        font-size: .64rem;
        display: -webkit-box;
        -webkit-box-orient: vertical;
        -webkit-line-clamp: 3;
        line-height: 1rem;
        overflow: hidden;
        padding: 0 .5rem!important;
    }
    .date{
        width: 100%;
        text-align: left;
        font-size: .64rem;
        margin: 0;
        font-family: '楷体';
        padding: .5rem!important;
    }
    @media screen and (max-width: 600px) { /*当屏幕尺寸小于600px时，应用下面的CSS样式*/
      .col-xs-6{
          width: 45%!important;
      }
        .jf_audio .item .jiemu .col-sm-2{
        margin-bottom: 1rem;
        margin-right: 0!important;
        display: flex;
        justify-content: space-between;
        align-items: center;
        padding: 0!important;
        }
    }
    .col-xs-6 .el-image{
        width: 100%!important;
        height: 100%;
    }
    .col-xs-6 .title{
        height: 3.8rem;
        display: -webkit-box;
        -webkit-box-orient: vertical;
        -webkit-line-clamp: 3;
        line-height: 1.3rem;
        overflow: hidden;
    }
    /*分页样式*/
    .el-pagination{
        width: 100%;
        height: 50px;
        display: flex;
        align-items: center;
        justify-content: flex-start;
    }
    .el-pager{
        background: #fff;
    }
    .el-pagination li{
        margin: 0!important;
    }
    /*分割线*/
    .divider{
        height: auto;
        display: flex;
        flex-direction: column;
        justify-content: space-between;
        align-items: flex-start;
        font-size: 1.1rem;
        color: #a06619;
        padding: .64rem 0;
    }
    .divider h4{
        font-size: 1.1rem;
        margin-bottom: .32rem;
    }
    .divider h4 em{
        font-style: normal;
        margin-right: .5rem;
    }
    .divider h5{
        font-size: .64rem;
        margin-right: .5rem;
    }
    .el-image{
        padding: 0!important;
        box-shadow: none!important;
    }
    .el-icon-video-play,.el-icon-video-pause{
        position: absolute;
        bottom: .3rem;
        right: .3rem;
        font-size: 1.6em;
        color: #a06619;
        opacity: .6;
    }
</style>
<script>
    import APlayer from 'aplayer'
    export default {
      name: "HotAudio",
      data() {
          return {
            btnStyle:'el-icon-video-play',
            visible: false,
            active: '',
            id:'',
            index:0,
            play:0,
            audio_menu:'',
            flag: false,
            demo: '',
            // 节目总数
            data1_len: 0,
            data2_len: 0,
            data3_len: 0,
            hotAudios:[],
              first_page_hotAudios:[],
              dataLength:11,
            trump_twitter:"",
            today_history:"",
              fits:'fill',
              first_page_data:'',
            data_len:''
        };
      },
      methods:{
          //  上下翻页
          handleCurrentChange1(val){
              var offset = (val-1)*5
              console.log(offset)
            this.first_page_hotAudios = this.hotAudios.slice(offset,offset+5)
        },
          handleCurrentChange2(val){
                var offset = (val-1)*5
                fetch("/ajax/jftime/handleCurrentChange2?offset="+offset).then(function (e) {
                    return e.json()
                }).then((e)=>{
                    this.today_history = e
                })
          },
          handleCurrentChange3(val){
            var offset = (val-1)*5
            fetch("/ajax/jftime/handleCurrentChange3?offset="+offset).then(function (e) {
                return e.json()
            }).then((e)=>{
                this.trump_twitter = e
            })
        },
          // 音频控件
          playAudio(audio_src,audio_name,creator,index){
            this.index = index
            // this.demo.destroy()
            const ap = new APlayer({
                container: document.getElementById('player'),
                fixed: false,
                autoplay: true,
                listFolded: true,
                listMaxHeight: 90,
                audio: [
                    {name: audio_name,
                    artist: creator,
                    url: audio_src,
                    cover: '',
                    }
                ]
                })
            ap.on('pause', ()=>{
                this.index = ''

            });
            ap.on('play', ()=>{
                this.index = index
            });
            console.log(this.index)
            this.demo = ap
        }
      },
      async mounted() {
        // const ap = new APlayer({
        //     container: document.getElementById('player'),
        //     fixed: false,
        //     listMaxHeight: 100,
        //     audio: [{
        //         name: '我们约在春天相见',
        //         artist: '文昭-秋旻',
        //         url: 'http://120.77.223.229:100/net_index/events.weba',
        //         cover: 'http://120.77.223.229:100/net_index/default_audio_bg.png',
        //     }]
        // })
        // this.demo = ap
              // 获取音频
          fetch("/ajax/jftime/hotAudios").then(function (e) {
                return e.json()
          }).then((e)=>{
              this.first_page_hotAudios = e.slice(0,5)
              this.hotAudios = e
              this.dataLength = 11
              console.log(e)
          })
        }
  }
</script>
