  import APlayer from 'aplayer'
  export default {
      name: "jfmt",
      data(){
          return {
              btnStyle:'el-icon-video-play',
              visible: false,
              active: '',
              id:'',
              index:0,
              play:0,
              audio_menu:'',
              flag: false,
              demo: '',
              fits:'fill',
              data_len: this.dataLength,
              demo_data_len:'',
              all_data:'',
              data: '',
              page_data:'',
              title:'',
              title_son:'',
              offset:0
      };
      },
      methods:{
        //  上下翻页
        handleCurrentChange(val){
            var offset = (val-1)*5
          fetch("/ajax/jftime/handleCurrentChange1?offset="+offset).then(function (e) {
              return e.json()
          }).then((e)=>{
              this.data = e
          })
      },
        // 音频控件
        playAudio(audio_src,audio_name,creator,index){
          this.index = index
          const ap = new APlayer({
              container: document.getElementById('player'),
              fixed: false,
              autoplay: true,
              listFolded: true,
              listMaxHeight: 90,
              audio: [
                  {name: audio_name,
                  artist: creator,
                  url: audio_src,
                  cover: '',
                  }
              ]
              })
          ap.on('pause', ()=>{
              this.index = ''

          });
          ap.on('play', ()=>{
              this.index = index
          });
          console.log(this.index)
          this.demo = ap
      }
    },
      mounted() {
          fetch("/ajax/jftime/jfmt").then(function (e) {
              return e.json()
          }).then((e)=>{
              this.data = e
          }),
              //  获取节目数
          fetch("/ajax/jftime/dataLength").then(function (e) {
            return e.json()
        }).then((e)=>{
              // this.demo_data_len = e
              this.title = '江峰漫谈'
              this.title_son = '蹭熱點，不蹭熱鬧；看新聞，更看門道。'
              this.data_len = e['data1_len']
          })
      }
}

