    height: 2rem;
    display: flex;
    justify-content: flex-start;
    align-items: center;
    font-size: 1.2rem;
    color: #a06619;
}
.divider h4{
    font-size: 1.2rem;
    margin-right: .5rem;
}
.col-xs-6 .el-image{
    width: 100%!important;
    height: 100%;
}
.col-xs-6 .title{
    height: 3.8rem;
    display: -webkit-box;
    -webkit-box-orient: vertical;
    -webkit-line-clamp: 3;
    line-height: 1.3rem;
    overflow: hidden;
    margin-bottom: 1rem;
}
.col-md-2{
    width: 100%!important;
    padding: .5rem!important;
    margin: 0 .5rem .5rem .5rem!important;
}
.jf_audio .item .jiemu div{
    display: flex;
    flex-direction: column;
    justify-content: space-between;
    align-items: center;
    position: relative;
    transition: all .5s;
    background: #fff;
}
.jf_audio  .item .jiemu div .el-image{
    width: 10rem;
    height: 6rem;
    margin-bottom: .5rem;
}
.title{
