<script>
    import APlayer from 'aplayer'
    import router from "../router";
    export default {
        name: "wenzhao",
        data(){
            return {
                btnStyle:'el-icon-video-play',
                visible: false,
                active: '',
                id:'',
                index:0,
                play:0,
                audio_menu:'',
                flag: false,
                demo: '',
                fits:'fill',
                data_len: this.dataLength,
                demo_data_len:'',
                data: '',
                page_data:'',
                title:'',
                title_son:'',
                offset:0,
            };
        },
        methods:{
          //  上下翻页
          handleCurrentChange(val){
              var offset = (val-1)*10
            fetch("/ajax/wz/handleCurrentChange?offset="+offset).then(function (e) {
                return e.json()
            }).then((e)=>{
                this.data = e
            })
        },
          // 音频控件
          playAudio(audio_src,audio_name,creator,index){
            this.index = index
            const ap = new APlayer({
                container: document.getElementById('player'),
                fixed: false,
                autoplay: true,
                listFolded: true,
                listMaxHeight: 90,
                audio: [
                    {name: audio_name,
                    artist: creator,
                    url: audio_src,
                    cover: '',
                    }
                ]
                })
            ap.on('pause', ()=>{
                this.index = ''

            });
            ap.on('play', ()=>{
                this.index = index
            });
            console.log(this.index)
            this.demo = ap
        }
      },
        computed:{
          noMore () {
            return this.count >= 10
          },
          disabled () {
            return this.loading || this.noMore
          }
        },
        mounted() {

            fetch("/ajax/wz/dataAudio").then(function (e) {
                return e.json()
            }).then((e)=>{
                this.data = e
            }),
            fetch("/ajax/wz/dataLength").then(function (e) {
              return e.json()
          }).then((e)=>{
                this.title = '『文昭談古論今』'
                this.title_son = '华语世界最受欢迎的评论节目之一'
                this.data_len = e['data_len']
            })
        }
  }
</script>
